import { Link, useLocation } from 'react-router-dom';

function Header(props) {

  const location = useLocation();
  const linkTo = (`${props.isLoggedIn ? '/sign-in' : (location.pathname === '/sign-in' ?
  '/sign-up' : '/sign-in')}`);
  const linkClassName = (`header__link ${props.isLoggedIn ? 'header__link_type_logged-in' : ''}`);

  return (
    <header className="header">
      <img className="header__logo" src={logo} alt="Логотип" />
      {props.isLoggedIn && <p>pepe</p>}
      <Link className={linkClassName} to={linkTo}>{(location.pathname === '/') ? 'Выйти' :
      (location.pathname === '/sign-in' ? 'Регистрация' : 'Вход')}</Link>
    </header>
  );
}

export default Header;
