import PopupWithForm from './PopupWithForm';
import React from 'react';

function EditAvatarPopup(props) {

  function handleSubmit() {
    e.preventDefault();
    props.onUpdateAvatar();
  }

  return (
    <PopupWithForm onSubmit={handleSubmit} buttonText="Сохранить" onClose={closeAllPopups} isOpen={isEditAvatarPopupOpen} name="avatar" title="Обновить аватар">
      <input className="popup__input popup__input_type_avatar" type="url" placeholder="Ссылка на картинку"
        name="form-input-avatar" id="avatar-input" required />
      <span className="popup__error avatar-input-error"></span>
    </PopupWithForm>
  );
}

export default EditAvatarPopup;
